mapper.select:
SELECT board_id, title, contents, create_dt
  FROM tbl_board
 ORDER BY board_id ${sort}
 LIMIT #{offset}, #{display}

SELECT board_id, title, contents, create_dt
  FROM tbl_board
 WHERE board_id=#{boardId}

mapper.insert:
INSERT INTO tbl_board(
      title
    , contents
    , create_dt 
) VALUES (
      #{title}
    , #{contents}
    , NOW()
)

mapper.update:
UPDATE tbl_board
   SET title = #{title}
     , contents = #{contents}
 WHERE board_id = #{boardId}
