mapper.insert:
INSERT INTO tbl_user (
    email
  , pwd
  , nickname
  , create_dt
) VALUES (
    #{email}
  , #{pwd}
  , #{nickname}
  , NOW()
)

mapper.update:
UPDATE tbl_user
   SET pwd = #{pwd}
     , nickname = #{nickname}
 WHERE user_id = #{userId}

mapper.select:
SELECT user_id, email, pwd, nickname, create_dt
  FROM tbl_user
 ORDER BY user_id ${sort}
 LIMIT #{offset}, #{display}

SELECT user_id, email, pwd, nickname, create_dt
  FROM tbl_user
 WHERE user_id = #{userId}
