mapper.select:
SELECT mem_id, mem_email, mem_name, mem_gender
  FROM tbl_member
 WHERE mem_id = #{memId}

SELECT addr_id, postcode, road_address, jibun_address, detail_address, extra_address, mem_id, addr_name
  FROM tbl_addr
 WHERE mem_id = #{memId}

mapper.insert:
INSERT INTO tbl_member (
    mem_email
  , mem_name
  , mem_gender 
) VALUES (
    #{memEmail}
  , #{memName}
  , #{memGender}
)

INSERT INTO tbl_addr (
    postcode
  , road_address
  , jibun_address
  , detail_address
  , extra_address
  , mem_id
  , addr_name 
) VALUES (
   #{postcode}
 , #{roadAddress}
 , #{jibunAddress}
 , #{detailAddress}
 , #{extraAddress}
 , #{memId}
 , #{addrName}
)

mapper.update:
UPDATE tbl_member
   SET mem_name = #{memName}
    ,  mem_gender = #{memGender}
 WHERE mem_id = #{memId}

UPDATE tbl_addr
   SET postcode = #{postcode}
    ,  road_address = #{roadAddress}
    ,  jibun_address = #{jibunAddress}
    ,  detail_address = #{detailAddress}
    ,  extra_address =  #{extraAddress}
    ,  addr_name = #{addrName}
 WHERE addr_id = #{addrId}
